package com.mendeley.api.request.params;

import android.net.Uri;

/**
 * Represents a page of results. This object is generated by the API and can be passed back to
 * subsequent calls in order to retrieve the next page.
 */
public class Page {
	public final Uri link;

    public Page(String link) {
        this.link = Uri.parse(link);
    }

    public static boolean isValidPage(Page page) {
        return page != null && page.link != null && page.link.toString().length() > 0;
    }

	@Override
	public String toString() {
		return "link: " + link;
	}
}
